import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import colorSharp2 from "../assets/img/color-sharp2.png";
import aries from "../assets/img/aries.png";
import orbit from "../assets/img/orbit.gif";
import ReactPanZoom from 'react-image-pan-zoom-rotate';
import 'animate.css';
import { useState } from 'react';
import TrackVisibility from 'react-on-screen';
import YouTube from 'react-youtube';

export const Projects = () => {

  const projects = [
    {
      title: "Aries",
      description: <ul style={{textAlign: centre}}><li style={{ listStyleType: "none", marginBottom: 20 }}>Aries is a small but prominent constellation in the northern hemisphere</li><li style={{ listStyleType: "none" }}>The brightest star in Aries, Hamal, is one of the closest stars to Earth at only 66 light-years away</li></ul>,
      imgUrl: aries,
    },
    {
      title: "Venus",
      description: <ul><li style={{ listStyleType: "none" }}>Hello</li><li style={{ listStyleType: "none" }}>Hello</li></ul>,
      imgUrl: aries,
    },
    {
      title: "Venus",
      description: <ul><li style={{ listStyleType: "none" }}>Hello</li><li style={{ listStyleType: "none" }}>Hello</li></ul>,
      imgUrl: aries,
    },
